import streamlit as stimport pandas as pdimport refrom gpt_utils import find_companies, score_companyfrom hunter_utils import get_domain, get_contactsimport iofrom coresignal_utils import estimate_local_employees, has_relevant_profile_in_locationst.set_page_config(page_title="AI Company Finder", layout="centered")st.title("ğŸ¤– AI Agent: Company Finder, Scorer & Enricher")# --- Step 1: Define Company Criteria ---st.header("ğŸ” Step 1: Describe Your Ideal Client Companies")location = st.text_input("ğŸ“ Location (optional)", placeholder="e.g. London, Manchester")sector = st.text_input("ğŸ­ Sector (optional)", placeholder="e.g. Legal, HR, Tech")size = st.text_input("ğŸ‘¥ Approximate Company Size (optional)", placeholder="e.g. 50+, mid-sized, under 500")goal = st.text_input("ğŸ¯ Why are you targeting them?", placeholder="e.g. Offer a workplace childcare solution")n_companies = st.slider("ğŸ“¦ Number of companies to generate", min_value=1, max_value=20, value=5)if st.button("Find Companies"):    with st.spinner("Thinking..."):        raw_response = find_companies(location, sector, size, goal, n_results=n_companies)    matches = re.findall(r"\d+\.\s+(.*?)(?:\s*\u2013\s*|\s*-\s*)(.*)", raw_response)    companies = [(name.strip(), desc.strip()) for name, desc in matches]    if not companies:        st.warning("âš ï¸ GPT response couldn't be parsed. Here's the raw output:")        st.markdown(raw_response)    else:        st.session_state["companies"] = companies# --- Load Companies ---companies = st.session_state.get("companies", [])if companies:    # --- Step 2: Score Companies ---    st.header("ğŸ“Š Step 2: Score Each Company")    scores = {}    for i, (name, desc) in enumerate(companies):        with st.expander(f"ğŸ”¹ {name}"):            st.write(desc)            if st.button(f"Score {name}", key=f"score-{i}"):                with st.spinner("Scoring..."):                    result = score_company(name, desc)                st.success("âœ… Scored!")                st.write(result)                scores[name] = result    # --- Step 3: Estimate Local Employees ---    st.header("ğŸ¢ Step 3: Estimate Local Employee Count")    selected_names = st.multiselect(        "Select companies to estimate size:",        options=[c[0] for c in companies],        default=[c[0] for c in companies]    )    local_employee_counts = {}    if st.button("ğŸ” Estimer les effectifs par bureau avec Coresignal"):        with st.spinner("Estimation en cours..."):            for name, desc in companies:                if name in selected_names:                    count = estimate_local_employees(name, location)                    local_employee_counts[name] = count        st.success("âœ… Estimations terminÃ©es !")    # --- Step 4: Enrich Contacts via Hunter.io ---    st.header("ğŸ“© Step 4: Enrich Contacts via Hunter.io")    role_keywords = st.multiselect(        "ğŸ¯ Filter contacts by decision-making roles:",        ["HR", "Manager", "Director", "CEO", "Founder"],        default=["HR", "Manager"]    )    role_keywords = [role.lower() for role in role_keywords]    refresh_cache = st.checkbox("ğŸ”„ RafraÃ®chir les rÃ©sultats (ignorer le cache)")    if refresh_cache:        st.cache_data.clear()        st.info("âœ… Cache effacÃ© â€” les donnÃ©es seront rechargÃ©es depuis Hunter.io")    enriched_contacts = []    if st.button("Enrich Selected Companies"):        for name, desc in companies:            if name not in selected_names:                continue            # Ã‰tape 1 : VÃ©rifier la prÃ©sence de profils pertinents avec Coresignal            if not has_relevant_profile_in_location(name, location, roles=role_keywords):                st.warning(f"â© {name} ignorÃ©e : aucun profil pertinent dÃ©tectÃ© Ã  {location}.")                continue            st.subheader(f"ğŸ” {name}")            with st.spinner("Searching domain..."):                domain = get_domain(name)                if not domain:                    st.warning("ğŸ”„ Domaine non trouvÃ© via Hunter.io, tentative via GPT...")                    try:                        from gpt_utils import client                        gpt_prompt = f"What is the most likely website domain of the UK company named {name}?"                        response = client.chat.completions.create(                            model="gpt-4-1106-preview",                            messages=[{"role": "user", "content": gpt_prompt}],                            temperature=0.2,                            max_tokens=20                        )                        domain = response.choices[0].message.content.strip()                        st.info(f"âœ… Domaine suggÃ©rÃ© par GPT : `{domain}`")                    except Exception as e:                        st.error(f"âŒ GPT failed to guess the domain: {e}")                        continue            if not domain:                st.error("âŒ Aucun domaine trouvÃ© pour cette entreprise.")                continue            with st.spinner("Searching contacts via Hunter.io..."):                contacts = get_contacts(domain, roles=role_keywords)                if not contacts:                    st.warning("No contacts found.")                    continue                for c in contacts[:5]:                    st.markdown(                        f"- **{c['name']}** â€” {c['position'] or 'N/A'}  "                        f"ğŸ“§ `{c['email']}`  ğŸ”— {c['linkedin'] or 'No LinkedIn'}"                    )                    enriched_contacts.append({                        "Company": name,                        "Description": desc,                        "Score": scores.get(name, ""),                        "Contact Name": c["name"],                        "Position": c["position"] or "",                        "Email": c["email"],                        "LinkedIn": c["linkedin"] or "",                        "Domain": domain                    })    # --- Export results to Excel ---    st.subheader("ğŸ“„ Exporter les rÃ©sultats (Excel multi-feuilles)")    company_rows = []    for name, desc in companies:        count = local_employee_counts.get(name, "")        company_rows.append({            "Company": name,            "Description": desc,            "Estimated Employees at Location": count        })    companies_df = pd.DataFrame(company_rows)    output = io.BytesIO()    with pd.ExcelWriter(output, engine="xlsxwriter") as writer:        companies_df.to_excel(writer, sheet_name="Companies", index=False)        if enriched_contacts:            contacts_df = pd.DataFrame(enriched_contacts)            contacts_df.to_excel(writer, sheet_name="Contacts", index=False)        writer.close()    st.download_button(        label="ğŸ“¤ TÃ©lÃ©charger en Excel",        data=output.getvalue(),        file_name="entreprises_et_contacts.xlsx",        mime="application/vnd.openxmlformats-officedocument.spreadsheetml.sheet"    )    if companies:    st.markdown(f"### âœ… {len(companies)} entreprises gÃ©nÃ©rÃ©es.")    if enriched_contacts:        st.markdown(f"### ğŸ“© {len(enriched_contacts)} contacts enrichis.")